mapper.delete:
DELETE FROM tb_warehouse t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_warehouse t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.code = #{code}


    AND t.name = #{name}


    AND t.manager = #{manager}


    AND t.warehouse_level = #{warehouseLevel}


    AND t.province_code = #{provinceCode}


    AND t.city_code = #{cityCode}


    AND t.county_code = #{countyCode}


    AND t.address = #{address}


    AND t.zipcode = #{zipcode}


    AND t.fax = #{fax}


    AND t.tel = #{tel}


    AND t.mobile = #{mobile}


    AND t.enable = #{enable}


    AND t.sort = #{sort}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC

SELECT 
FROM tb_warehouse t
WHERE t.row_valid = 1


    AND t.code = #{code}


    AND t.name LIKE #{name}


    AND t.warehouse_level = #{warehouseLevel}

ORDER BY t.sort ASC, t.id DESC
